import React, { Component } from 'react'
import FavoriteArtist from './FavoriteArtist';

export default class Form extends Component {
  constructor() {
    super();

    this.handleChange = this.handleChange.bind(this);

    this.state = {
      goToSong: '',
    }
  }

  handleChange({ target }) {
    const { name } = target;
    const value = target.type === 'checkbox' ? target.checked : target.value;

    this.setState({
      [name]: value,
    });
  }

  render() {
    return(
      <div id="form-div">
      <form>
        <FavoriteArtist />
        <fieldset>
          <label htmlFor="start-year">
            What year did you start listening to them?
            <input
              id="start-year"
              name="start-year"
              type="number"
              onChange={ this.handleChange }
            />
          </label>
        </fieldset>
        <fieldset>
          <label htmlFor="">
            Have you ever got to go their show?
            <input
              id="show"
              name="show"
              type="checkbox"
              value="yes"
              onChange={ this.handleChange }
            />
          </label>
        </fieldset>
        <fieldset>
          <label htmlFor="go-to">
            What song is your go to?
            <textarea
              id="go-to"
              name="go-to"
              onChange={ this.handleChange }
            />
          </label>
        </fieldset>
        <fieldset>
          <label htmlFor="file">
            <input
              id="file"
              type="file"
            />
          </label>
        </fieldset>
      </form>
    </div>
    );
  }
}
