import React, { Component } from 'react';
import './App.css';

class App extends Component {
  constructor() {
    super();

    this.handleChange = this.handleChange.bind(this);

    this.state = {
      goToSong: '',
    }
  }

  handleChange({ target }) {
    const { name } = target;
    const value = target.type === 'checkbox' ? target.checked : target.value;

    this.setState({
      [name]: value,
    });
  }

  render() {
    return(
      <div id="form-div">
      <form>
        <label htmlFor="favorite-artist">
          Choose your favorite artist.
          <select id="favorite-artist">
            <option disabled>Pick one</option>
            <option value="peep">Lil Peep</option>
            <option value="tracy">Lil Tracy</option>
            <option value="sboys">$uicideBoy$</option>
            <option value="ghostemane">Ghostemane</option>
          </select>
        </label>
        <label htmlFor="start-year">
          What year did you start listening to them?
          <input
            id="start-year"
            name="start-year"
            type="number"
          />
        </label>
        <label htmlFor="">
          Have you ever got to go their show?
          <input
            id="show"
            name="show"
            type="checkbox"
            value="yes"
          />
        </label>
        <label htmlFor="go-to">
          What song is your go to?
          <textarea
            id="go-to"
            name="go-to"
            onChange={ this.handleChange }
          />
        </label>
      </form>
    </div>
    );
  }
}

export default App;
