import React, { Component } from 'react'
import FavoriteArtist from './FavoriteArtist';
import Show from './Show';

export default class Form extends Component {
  constructor() {
    super();

    this.handleChange = this.handleChange.bind(this);
  }

  handleChange({ target }) {
    const { name } = target;
    const value = target.type === 'checkbox' ? target.checked : target.value;

    this.setState({
      [name]: value,
    });
  }

  render() {
    const {
      yearValue,
      goToValue,
    } = this.props;

    return(
      <div id="form-div">
      <form>

        <FavoriteArtist
          changeFunction={ this.handleChange }
        />

        <fieldset>
          <label htmlFor="start-year">
            What year did you start listening to them?
            <input
              id="start-year"
              name="start-year"
              type="number"
              onChange={ this.handleChange }
            />
          </label>
          { !yearValue.length ? 'invalid year' : 'ok' }
        </fieldset>

        <Show
          changeFunction={ this.handleChange }
        />

        <fieldset>
          <label htmlFor="go-to">
            What song is your go to?
            <textarea
              id="go-to"
              name="go-to"
              onChange={ this.handleChange }
            />
          </label>
          { !goToValue.length ? 'invalid song' : 'ok' }
        </fieldset>

        <fieldset>
          <label htmlFor="file">
            <input
              id="file"
              type="file"
            />
          </label>
        </fieldset>
      </form>
    </div>
    );
  }
}
